import styled from "styled-components";

export const Container = styled.div`
    max-width: 1120px;
    margin: 20 auto;
    width: 98%;
    background-color: #fff;
    box-shadow: 8px 8px 5px #ccc;
    border-radius: 5px; 
    display: flex;
    justify-content: space-around;
    gap: 10px;
    padding: 15px 0px;

    @media (max-width: 750px){
    display: grid;

`
