import React from "react";
import * as C from "./styles";
import ResumeItem from "../ResumeItem";
import { FaRegArrowAltCircleUp, FaRegArrowAltCircleDown, FaDollarSign, FaTrash } from "react-icons/fa";

interface ResumeProps {
    expense: string | number;
    income: string | number;
    total: string | number;
}

interface Transaction {
    id: number;
    desc: string;
    amount: number;
    expense: boolean;
}

interface GridItemProps {
    item: Transaction;
    onDelete: (id: number) => void;
}

const Resume: React.FC<ResumeProps> = ({ expense, income, total }) => {
    return (
        <C.Container>
            <ResumeItem title="Entradas" Icon={FaRegArrowAltCircleUp} value={income} />
            <ResumeItem title="SaÃ­das" Icon={FaRegArrowAltCircleDown} value={expense} />
            <ResumeItem title="Total" Icon={FaDollarSign} value={total} />
        </C.Container>
    );
};

const GridItem: React.FC<GridItemProps> = ({ item, onDelete }) => {
    return (
        <C.Tr>
            <C.Td>{item.desc}</C.Td>
            <C.Td>{item.amount}</C.Td>
            <C.Td alignCenter>
                {item.expense ? (
                    <FaRegArrowAltCircleDown color="red" />
                ) : (
                    <FaRegArrowAltCircleUp color="green" />
                )}
            </C.Td>
            <C.Td alignCenter>
                <FaTrash onClick={() => onDelete(item.id)} />
            </C.Td>
        </C.Tr>
    );
};

export { Resume, GridItem };
