import React, { useState } from "react";
import Grid from "../Grid";
import * as C from "./styles"

const Form = ({ handleAdd, setTransactionsList }) => { 
    const [desc, setDesc] = useState("");
    const [amount, setAmount] = useState("");
    const [isExpensive, setExpense] = useState(false);

    const generateID = () => Math.round(Math.random() * 1000)

    const handleSave = () => {
        if(!desc || !amount ){
            alert("Favor informe a descricao do valor");
            return
        }else if(amount < 1){
            alert("o valor tem que ser possitivo");
            return
        }

        const transaction = {
            id: generateID,
            desc: desc,
            amount: amount,
            expense: isExpensive,
        }

        handleAdd(transaction)
        setDesc("")
        setAmount("")
    }


    return(
        <>
        <C.Container>
            <C.InputContent>
                <C.label>Descricao</C.label>
                <C.Input 
                value={desc} 
                onChange={(e)=> setDesc(e.target.value)}
                ></C.Input>
            </C.InputContent>
            <C.InputContent>
                <C.label>Valor</C.label>
                <C.Input 
                value={amount}
                type="number" 
                onChange={(e)=> setAmount(e.target.value)}
                ></C.Input>
            </C.InputContent>
            <C.RadioGroup>
                 <C.Input
                    type="radio"
                    id="rIncome"
                    defaultChecked
                    name="grup1"
                    onChange={() => setExpense((!isExpensive))}
                 />
                 <C.label htmlFor="rIncome">Entrada</C.label>
                 <C.Input
                    type="radio"
                    id="rExpensive"
                    name="grup1"
                    onChange={() => setExpense((!isExpensive))}
                 />
                 <C.label htmlFor="rExpensive">Saida</C.label>
            </C.RadioGroup>
            <C.Button onClick={handleSave}>ADICIONAR</C.Button>
        </C.Container>
        <Grid itens={transactionsList} setItens={setTransactionsList}></Grid>
        </>
    )
}

export default Form;
