    const [isExpensive, setExpense] = useState(false);

    const handleSave = () => {
        if(!desc || !amount ){
            alert("Favor informe a descricao do valor");
            return
        }else if(amount < 1){
            alert("o valor tem que ser possitivo");
            return
        }
    }


    return(
        <>
        <C.Container>
            <C.InputContent>
                <C.label>Desc</C.label>
                <C.Input></C.Input>
            </C.InputContent>
        </C.Container>
        </>
    )
}

